package orgmode

import java.io.File

fun main(args: Array<String>) {

    var org: Org
    
    if(args.size > 0) {
	org = OrgParser(FileSource(args[0])).parse()
    } else {

	org = OrgParser(StringSource("""
* Functional
*test* markup /simple/
*more +complicated+ markup*
""")).parse()
    }
    

    println(org.toString())
    println(org.toJson())

    File("/tmp/kt.html").writeText(org.toHtml())
}
