    @Test fun testParseText() {
	
	val org: Org = OrgParser(StringSource("""Test
""")).parse()

	val res: Org = Document(listOf(Paragraph(listOf(Text("Test")))))
	
	println(org.toJson())
	println(res.toJson())
	
	assertEquals(org, res)
    }

    @Test fun testParseSections() {
	
	val org: Org = OrgParser(StringSource("""* Test1
** Test 2
* Test 3
""")).parse()

	val res: Org = Document(
	    listOf(
		Section("Test1", 1, listOf(
			    Section("Test 2", 2, emptyList())
		)),
		Section("Test 3", 1, emptyList())
	    )
	)
	
	println(org.toJson())
	println(res.toJson())
	
	assertEquals(org, res)
    }

    @Test fun testParseSectionsWithText() {
	
	val org: Org = OrgParser(StringSource("""* Test1
Text 1
** Test 2
Text 2
Text 3
* Test 3
""")).parse()

	val res: Org = Document(
	    listOf(
		Section("Test1", 1, listOf(
			    Paragraph(listOf(
					  Text("Text 1")
			    )),
			    Section("Test 2", 2, listOf(
					Paragraph(listOf(
						      Text("Text 2"),
						      Text("Text 3")
					))
			    ))
		)),
		Section("Test 3", 1, emptyList())
	    )
	)
	
	println(org.toJson())
	println(res.toJson())
	
	assertEquals(org, res)
    }


    @Test fun testMulitline() {

	val org: Org = OrgParser(StringSource("""
* Doc
Text 1
Same line\\
Another line
""")).parse()

	val res: Org = Document(
	    listOf(
		Section("Doc", 1, listOf(
			    Paragraph(listOf(
					  Text("Text 1"),
					  Text("Same line\n"),
					  Text("Another line")
			    ))
		))
	    )
	)
	
	println(org.toJson())
	println(res.toJson())
	
	assertEquals(org, res)

    }
    
    @Test fun testList() {
	
	val org: Org = OrgParser(StringSource("""
* Unordered List
- elem 1

- elem 2

* Ordered List

1. elem 1

2. elem 2
""")).parse()

	val res: Org = Document(
	    listOf(
		Section("Unordered List", 1,
			listOf(
			    OrgList(
				listOf(
				    ListEntry("elem 1"),
				    ListEntry("elem 2")
			    ))
		)),
		Section("Ordered List", 1,
			listOf(
			    OrgList(
				listOf(
				    ListEntry("elem 1", "1."),
				    ListEntry("elem 2", "2.")
			    ))
		))

	))
	
	println(org.toJson())
	println(res.toJson())
	
	assertEquals(org, res)
    }

    @Test fun testListWithContent() {
	
	val org: Org = OrgParser(StringSource("""
* List
- elem 1
  Text
Not in list
- elem 2
  
  Another Text

  Still in list


         Not in list too

""")).parse()

	val res: Org = Document(
	    listOf(
		Section("List", 1,
			listOf(
			    OrgList(
				listOf(
				    ListEntry("elem 1", entities =
						  listOf(
						      Paragraph(
							  listOf(
							      Text("Text")
						      ))
				    ))
			    )),
			    Paragraph(
				listOf(
				    Text("Not in list")
			    )),
			    OrgList(
				listOf(
				    ListEntry("elem 2", entities =
						  listOf(
						      Paragraph(
							  listOf(
							      Text("Another Text"),
							      Text("Still in list")
						      ))
				    ))
			    )),
			    Paragraph(
				listOf(
				    Text("Not in list too")
			    ))
		))
	))
	
	println(org.toJson())
	println(res.toJson())
	
	assertEquals(org, res)
    }

    @Test fun testTwoEmptyLinesBreaksAllLists() {
	
	val org: Org = OrgParser(StringSource("""
* List
- elem 1
  Text
  - inner list
    Inner list Text


  Text

""")).parse()

	val res: Org = Document(
	    listOf(
		Section("List", 1,
			listOf(
			    OrgList(
				listOf(
				    ListEntry("elem 1", entities =
						  listOf(
						      Paragraph(
							  listOf(
							      Text("Text")
						      )),
						      OrgList(
							  listOf(
							      ListEntry("inner list", entities =
									    listOf(
										Paragraph(
										    listOf(
											Text("Inner list Text")
										))
							      ))
						      ))
				    ))
			    )),
			    Paragraph(
				listOf(
				    Text("Text")
			    ))

		))
	))
	
	println(org.toJson())
	println(res.toJson())
	
	assertEquals(org, res)
    }
}
